function run(input) {
    const repositories = JSON.parse(input);

    return {
        "itemType": "application/vnd.lime.document-select+json",
        "items": repositories.map(function(repo) {
            return {
                "header": {
                    "type": "application/vnd.lime.media-link+json",
                    "value": {
                        "title": repo.name,
                        "text": repo.description,
                        "type": "image/jpeg",
                        "uri": "https://avatars.githubusercontent.com/u/4369522?s=200&v=4"
                    }
                },
                "options": [
                    {
                        "label": {
                            "type": "application/vnd.lime.web-link+json",
                            "value": {
                                "title": "Acessar Repositório",
                                "uri": "https://github.com/" + repo.full_name
                            }
                        }
                    }
                ]
            };
        })
    };
}

